import axios from 'axios';
import React, { useState, useRef } from 'react';

const PostGrid = ({ onPostSubmit, user }) => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);
  const fileInputRef = useRef();

const handlePostSubmit = () => {
    const newPost = {
      id: Date.now(), 
      title: "Your New Post!",
      body: postText,
      user: user,
      timestamp: new Date().toLocaleString(),
      author: 'User', 
      image: image ? URL.createObjectURL(image) : null 
    };

    console.log("Post Text:", postText);
    console.log("Image File:", image);

    const formData = new FormData();
    formData.append('text', postText);
    if (image) {
        formData.append('image', image);
    }


    console.log(formData);
    axios.post('http://localhost:3000/article', formData, { withCredentials: true })
    .then(_response => {
      onPostSubmit(newPost);
    }).catch(error => {
    console.error("Post failed", error);
});

    setPostText('');
    setImage(null);q
  };
  const handleClear = () => {
    setPostText('');
    setImage(null);
    fileInputRef.current.value = ''; // Reset the file input

  };

  return (
    <div className="post-grid">
      <input
        type="text"
        placeholder="Write your post..."
        value={postText}
        onChange={(e) => setPostText(e.target.value)}
        className="post-input"
      />
      <input className="post-button"
        type="file"
        accept="image/*"
        onChange={(e) => setImage(e.target.files[0])}
        ref={fileInputRef} 
      />
      <div className="post-button-container">
        <button onClick={handlePostSubmit} className="post-button">Post</button>
        <button onClick={handleClear} className="post-button">Clear</button>
      </div>

    </div>
  );
};

export default PostGrid;
