import React, { useEffect, useState } from 'react';
import axios from 'axios';
const Posts = ({ post}) => {
  const [showComments, setShowComments] = useState(true); 
  // const defaultComments = [
  //   "Good job!",
  //   "I totally agree with this.",
  //   "I disagree."  ];
  const [comments, setComments] = useState([]); 
  const [showCommentBox, setShowCommentBox] = useState(false);
  const [commentContent, setCommentContent] = useState('');
  const [showInput, setShowInput] = useState(false);
  const [currentAction, setCurrentAction] = useState('');

  // // Function to show the input box and submit button
  // const showInputBox = () => {
  //   setShowInput(true);
  // };

  // // Function to hide the input box and submit button
  // const hideInputBox = () => {
  //   setShowInput(false);
  // };
  // const toggleComments = () => {
  //   setShowComments(!showComments);
  //   if (showComments) {
  //     // Hide input box when comments are being hidden
  //     setShowInput(false);
  //   }
  // };
  const toggleComments = () => {
    setShowComments(!showComments);
    setShowInput(false); // Also hide the input box when toggling comments
  };
  const toggleInputBox = () => {
    setShowInput(!showInput);
  };
  const formatDate = (isoString) => {
    const date = new Date(isoString);
    return date.toLocaleString(); // Or any other format you prefer
  };
  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/articles/${post.pid}`, { withCredentials: true });
      const comments = response.data.articles[0].comments;
      console.log(comments);
      setComments(comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  }
  const onComment = (pid) => {
    setCurrentAction('leaveComment');
    toggleInputBox();


      }
  const onEditPost = (pid) => {
    setCurrentAction('editPost');
    toggleInputBox();



  }
  const onEditComment = (pid) => {
    setCurrentAction('editComment');
    toggleInputBox();


  }
    useEffect(() => {
    fetchComments();
  }, [post.pid]);
  const handleSubmit = async () => {
    toggleInputBox(); // Hide the input box

    switch (currentAction) {
      case 'leaveComment':
        // Make a request to leave a comment
        break;
      case 'editPost':
        // Make a request to edit the post
        break;
      case 'editComment':
        // Make a request to edit a comment
        break;
      default:
        // Handle unknown action
        break;
    }

    // Clear the current action after handling
    setCurrentAction('');
  };

  return (
    <div className="post-container">
      {/* <div className="post-title">{post.title}</div> */}
      <div className="post-author">Author: {post.author}</div>
      <div className="post-timestamp">Posted on: {formatDate(post.date)}</div>
      <div className="post-body">{post.text}</div>
      <div className="post-image">
        {post.image && <img src={post.image} alt="Post"/>}
      </div>
      {showComments && (
        <div className="comments-section">
          <h4>Comments</h4>
          <ul>
            {comments && comments.map((comment) => (
              <li key={comment.id}>{comment.author}: {comment.message}</li>
            ))}
          </ul>
        </div>
      )}
      <div className="post-buttons">
        <button className="post-button" onClick={onComment(post.pid)}>Leave a Comment</button>
        <button className="post-button" onClick={() => {toggleInputBox(); onEditPost(post.pid)}}>Edit Post</button>
        <button className="post-button" onClick={() => {toggleInputBox(); onEditComment(post.pid)}}>Edit My Comment</button>
        <button className="post-button" onClick={toggleComments}>
          {showComments ? "Hide Comments" : "Show Comments"}
        </button>
      </div>
      {showInput && (
        <div className="input-container">
          <textarea className="input-box" />
          <button className="submit-button" onClick={toggleInputBox}>Submit</button>
        </div>
      )}
    </div>
  );
};

export default Posts;
