import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import MainHeader from '../components/MainHeader';
import Posts from '../components/Posts';
import UserInfoGrid from '../components/UserInfoGrid';
import PostGrid from '../components/PostGrid';
import userPostsTest from '../posts.json';
import usersInfo from '../users.json'
import axios from 'axios';

const MainPage = () => {
  const [userPosts, setUserPosts] = useState([]);
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [newFollowerName, setNewFollowerName] = useState('');
  const [followedUsers, setFollowedUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [allUserPosts, setAllUserPosts] = useState([]); // To store all posts
const [currentPage, setCurrentPage] = useState(1);
const [postsPerPage] = useState(10);
const indexOfLastPost = currentPage * postsPerPage;
const indexOfFirstPost = indexOfLastPost - postsPerPage;
const currentPosts = allUserPosts.slice(indexOfFirstPost, indexOfLastPost);
const handleNextPage = () => {
  setCurrentPage(currentPage => Math.min(currentPage + 1, Math.ceil(allUserPosts.length / postsPerPage)));
};

const handlePreviousPage = () => {
  setCurrentPage(currentPage => Math.max(currentPage - 1, 1));
};


  const location = useLocation();
  const userData = location.state.userData;
  const defaultPostImage = 'https://media.istockphoto.com/id/1420755455/photo/grunge-dirty-background-overlay.webp?b=1&s=170667a&w=0&k=20&c=7wfOkV1I0RJVAsuZlCzMD8zRenjlWo1hUYCAIsGBckw=';
  useEffect(() => {
  fetchFollowers();
  fetchUserPosts()
 }, [userData.username]);

 const fetchUserPosts = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/articles`, { withCredentials: true });
      const articles = response.data.articles;
      console.log(articles)
      if (articles.length === 0) {
        setUserPosts([]);
        return ([])
      } else {
      const sortedPosts = articles.sort((a, b) => new Date(b.date) - new Date(a.date));
      setUserPosts(sortedPosts);
    }
 } catch (error) {
      console.error('Error fetching posts:', error);
  }
};

 const fetchFollowers = async () => {
  try {
    console.log(userData.username)
      const response = await axios.get(`http://localhost:3000/following/${userData.username}`, { withCredentials: true });
      const usernames = response.data.following;
      console.log(usernames)
      if (usernames.length === 0) {
        setFollowedUsers([]);
        return ([])
      }
      const profiles = await Promise.all(
        usernames.map(username =>
            axios.get(`http://localhost:3000/profile/${username}`, { withCredentials: true })
                .then(res => res.data)
                .catch(err => {
                    console.error(`Error fetching profile for ${username}:`, err);
                    return null; // or handle the error as appropriate
                })
        )
    );
      setFollowedUsers(profiles);
      console.log(followedUsers, profiles)
      return profiles;
  } catch (error) {
      console.error('Error fetching followers:', error);
  }
};
 const handleAddFollower = async () => {
  if (newFollowerName == userData.username) {
    setErrorMessage("You cannot follow yourself.");  
    setNewFollowerName('');
    return;
  }
  try {
    const response = await axios.put(`http://localhost:3000/following/${newFollowerName}`, {}, { withCredentials: true });
    if (response.data.username == userData.username) {
      setErrorMessage("");  
      setNewFollowerName('');
      fetchFollowers();
      fetchUserPosts();
    } else {
      setNewFollowerName('');
      setErrorMessage("user not found.");
    }
  } catch (error) {
    setNewFollowerName('');
    setErrorMessage(error.response.data.message);
  };}

  const handleUnfollow = async username => {
    try {
      const response = await axios.delete(`http://localhost:3000/following/${username}`, { withCredentials: true });
      if (response.data.username === userData.username) {
         fetchFollowers();
         fetchUserPosts();
      } else {
        console.log('Unfollow failed');
      }
    } catch (error) {
      console.error('Error unfollowing user:', error);
    }
  };


  const handleSearch = (e) => {
    setSearchQuery(e.target.value)
  };

  const handleComment = postId => {
  };

  const handleEditPost = postId => {

  };
  const handleEditComment = (postId) => {
  }

  const handlePostSubmit = newPost => {
    setUserPosts([newPost, ...userPosts]);
  };

  const handleStatusUpdate = newStatus => {

  };

  return (
    <div>
      <MainHeader navigate={navigate} userData={userData} />
      <div className="main-content">
        <UserInfoGrid user={userData} onUpdateStatus={handleStatusUpdate} />
        <PostGrid user={userData} onPostSubmit={handlePostSubmit} />
      </div>
      <div className="sidebar">
        <h2 className = "follow-user-h2">Followed Users</h2>
        <ul className="follow-user-ul">
          {followedUsers.map(user => (
            <li className='follow-user-li'key={user.id}>
              <img className='follow-user-img' src={user.avatar} alt="Avatar" />
              <p>{user.username}</p>
              <p>{user.headline}</p>

              <button data-testid={`unfollow-${user.username}`} onClick={() => handleUnfollow(user.username)}>Unfollow</button>
            </li>
          ))}
        </ul>
        <input className='se'
          type="text"
          data-testid='add-follower-name'
          placeholder="Enter name"
          value={newFollowerName}
          onChange={e => setNewFollowerName(e.target.value)}
        />
        <button data-testid='add-follower' onClick={handleAddFollower}>Add Follower</button>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

      </div>
      <div className="search-bar">
        <input data-testid='search-test'
          type="text"
          placeholder="Search by author or text..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      <div data-testid='user-posts'>
      {currentPosts.filter(post => {
        return post.text.toLowerCase().includes(searchQuery.toLowerCase()) || post.author.toLowerCase().includes(searchQuery.toLowerCase()) ;
      }).map(post => (
        <Posts
          key={post.pid}
          post={post}
          loggedInUser={userData.username}
        />
      ))}
    </div>
    <div className="pagination-controls">
  <button onClick={handlePreviousPage} disabled={currentPage === 1}>Previous</button>
  <span>Page {currentPage} of {Math.ceil(allUserPosts.length / postsPerPage)}</span>
  <button onClick={handleNextPage} disabled={currentPage === Math.ceil(allUserPosts.length / postsPerPage)}>Next</button>
</div>


    </div>
  );
};

export default MainPage;
