import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserInfoGrid = ({ user, onUpdateStatus }) => {
  console.log(user)
  // make headline update persistent
  // const [status, setStatus] = useState(localStorage.getItem(`userStatus_${user.id}`) || user.company.catchPhrase);
  const [status, setStatus] = useState("");

  const [newStatus, setNewStatus] = useState('');

    const [avatarUrl, setAvatarUrl] = useState('');
    const [displayName, setDisplayName] = useState('');

  const fetchHeadline = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/headline/${user.username}`, { withCredentials: true });
      setStatus(response.data.headline);
    } catch (error) {
      console.error('Error fetching headline:', error);
    }
  };

  const fetchDisplayName = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/displayname/${user.username}`, { withCredentials: true });
      setDisplayName(response.data.displayname);
    } catch (error) {
      console.error('Error fetching display name:', error);
    }
  }

  const fetchAvatar = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/avatar/${user.username}`, { withCredentials: true });
      setAvatarUrl(response.data.avatar);
      console.log(response.data.avatar)
    } catch (error) {
      console.error('Error fetching avatar:', error);
    }
  };

  useEffect(() => {
    fetchHeadline();
    fetchAvatar();
    fetchDisplayName();
  }, [user.username]); 

  const handleStatusUpdate = () => {
    if (newStatus.trim() !== '') {
      onUpdateStatus(newStatus);
      setStatus(newStatus);
      setNewStatus('');
      axios.put(`http://localhost:3000/headline`, { headline: newStatus }, { withCredentials: true })
      .then(response => {
        if (response.data.result === 'success') {
          console.log('Status updated');
        } else {
          console.log('Status update failed');
        }
      })
    }

  };

  return (
    <div className="user-info-grid">
      <div>
        <h3>{displayName}</h3>
        <img src={avatarUrl}></img>
      </div>
      <p className="status">{status}</p>
      <div>
        <input
          type="text"
          value={newStatus}
          onChange={(e) => setNewStatus(e.target.value)}
          className="post-input"
        />
        <button onClick={handleStatusUpdate} className="post-button">
          Update Status
        </button>
      </div>
    </div>
  );
};

export default UserInfoGrid;
