import React, { useState } from 'react';

const UserInfoGrid = ({ user, onUpdateStatus }) => {
  // make headline update persistent
  const [status, setStatus] = useState(localStorage.getItem(`userStatus_${user.id}`) || user.company.catchPhrase);
  const [newStatus, setNewStatus] = useState('');

  const handleStatusUpdate = () => {
    if (newStatus.trim() !== '') {
      onUpdateStatus(newStatus);
      setStatus(newStatus);
      setNewStatus('');
      localStorage.setItem(`userStatus_${user.id}`, newStatus);
    }
  };

  return (
    <div className="user-info-grid">
      <div>
        <h3>{user.username}</h3>
        <img src='https://media.istockphoto.com/id/1437816897/photo/business-woman-manager-or-human-resources-portrait-for-career-success-company-we-are-hiring.webp?b=1&s=170667a&w=0&k=20&c=YQ_j83pg9fB-HWOd1Qur3_kBmG_ot_hZty8pvoFkr6A='></img>
      </div>
      <p className="status">{status}</p>
      <div>
        <input
          type="text"
          value={newStatus}
          onChange={(e) => setNewStatus(e.target.value)}
          className="post-input"
        />
        <button onClick={handleStatusUpdate} className="post-button">
          Update Status
        </button>
      </div>
    </div>
  );
};

export default UserInfoGrid;
