import React, { useState, useEffect, useRef } from "react";
import ProfileHeader from '../components/ProfileHeader';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProfilePage = () => {
const navigate = useNavigate();
const location = useLocation();
const [avatarUrl, setAvatarUrl] = useState('');

const userData = location.state.userData;
const [image, setImage] = useState(null);
const fileInputRef = useRef();

const handleUpdateAvatar = () => {


  const formData = new FormData();
  formData.append('image', image);



  axios.put('http://localhost:3000/avatar', formData, { withCredentials: true })
  .then(response => {
    setAvatarUrl(response.data.avatar);
    setImage(null);
    fileInputRef.current.value = '';
  }).catch(error => {
  console.error("update failed", error);
});
};

const handleUpdateDisplayName = () => {
  axios.put('http://localhost:3000/displayname', { displayname: name }, { withCredentials: true })
  .then(_response => {}).catch(error => {console.log(error)});
}

const handleUpdateEmail = () => {
  axios.put('http://localhost:3000/email', { email: email }, { withCredentials: true })
  .then(_response => {}).catch(error => {console.log(error)});
}

const handleUpdatePhone = () => {
  axios.put('http://localhost:3000/phone', { phone: phone }, { withCredentials: true })
  .then(response => {
    if (response.data.result === 'success') {
      console.log('Phone updated');
    } else {
      console.log('Phone update failed');
    }
  }).catch(error => {console.log(error)});
}

const handleUpdateZip = () => {
  axios.put('http://localhost:3000/zipcode', { zipcode: zip }, { withCredentials: true })
  .then(response => {
    if (response.data.result === 'success') {
      console.log('Zip code updated');
    } else {
      console.log('Zip code update failed');
    }
  }).catch(error => {console.log(error)});
}

const handleUpdatePassword = () => {
  axios.put('http://localhost:3000/password', { password: password }, { withCredentials: true })
  .then(response => {
    if (response.data.result === 'success') {
      console.log('Password updated');
    } else {
      console.log('Password update failed');
    }
  }).catch(error => {console.log(error)});
}

const fetchAvatar = async () => {
  try {
    const response = await axios.get(`http://localhost:3000/avatar/${userData.username}`, { withCredentials: true });
    setAvatarUrl(response.data.avatar);
  } catch (error) {
    console.error('Error fetching avatar:', error);
  }
};

const fetchDisplayName = async () => {
  try {
    const response = await axios.get(`http://localhost:3000/displayname/${userData.username}`, { withCredentials: true });
    setCurName(response.data.displayname);
  } catch (error) {
    console.error('Error fetching display name:', error);
  }
}

const fetchEmail = async () => {
  try {
    const response = await axios.get(`http://localhost:3000/email/${userData.username}`, { withCredentials: true });
    setCurEmail(response.data.email);
  } catch (error) {
    console.error('Error fetching email:', error);
  }
}

const fetchPhone = async () => {
  try {
    const response = await axios.get(`http://localhost:3000/phone/${userData.username}`, { withCredentials: true });
    setCurPhone(response.data.phone);
  } catch (error) {
    console.error('Error fetching phone:', error);
  }
}

const fetchZip = async () => {
  try {
    const response = await axios.get(`http://localhost:3000/zipcode/${userData.username}`, { withCredentials: true });
    setCurZip(response.data.zipcode);
  } catch (error) {
    console.error('Error fetching zip code:', error);
  }
}


