  const onEditPost = (pid) => {
  }
  const onEditComment = (pid) => {
  }
    useEffect(() => {
    fetchComments();
  }, [post.pid]);
  return (
    <div className="post-container">
      {/* <div className="post-title">{post.title}</div> */}
      <div className="post-author">Author: {post.author}</div>
      <div className="post-timestamp">Posted on: {formatDate(post.date)}</div>
      <div className="post-body">{post.text}</div>
      <div className="post-image">
        {post.image && <img src={post.image} alt="Post"/>}
      </div>
      {showComments && (
        <div className="comments-section">
          <h4>Comments</h4>
          <ul>
            {comments && comments.map((comment) => (
              <li key={comment.id}>{comment.author}: {comment.message}</li>
            ))}
          </ul>
        </div>
      )}
      <div className="post-buttons">
        <button className="post-button" onClick={() => onComment(post.pid)}>Leave a Comment</button>
        <button className="post-button" onClick={() => onEditPost(post.pid)}>Edit Post</button>
        <button className="post-button" onClick={() => onEditComment(post.pid)}>Edit My Comment</button>
        <button className="post-button" onClick={() => setShowComments(!showComments)}>

          {showComments ? "Hide Comments" : "Show Comments"}
        </button>
      </div>

