import React, { useState } from 'react';

const Posts = ({ post, onComment, onEdit }) => {
  const [showComments, setShowComments] = useState(true); 
  const defaultComments = [
    "Good job!",
    "I totally agree with this.",
    "I disagree."  ];

  return (
    <div className="post-container">
      <div className="post-title">{post.title}</div>
      <div className="post-author">Author: {post.user.username}</div>
      <div className="post-timestamp">Posted on: {post.timestamp}</div> 
      <div className="post-body">{post.body}</div>
      <div className="post-image">
        {post.image && <img src={post.image} alt="Post"/>}
      </div>
      {showComments && (
        <div className="comments-section">
          <h4>Comments</h4>
          <ul>
            {defaultComments.map((comment, index) => (
              <li key={index}>{comment}</li>
            ))}
          </ul>
        </div>
      )}
      <div className="post-buttons">
        <button className="post-button" onClick={() => onComment(post.id)}>Leave a Comment</button>
        <button className="post-button" onClick={() => onEdit(post.id)}>Edit</button>
        <button className="post-button" onClick={() => setShowComments(!showComments)}>
          {showComments ? "Hide Comments" : "Show Comments"}
        </button>
      </div>

    </div>
  );
};

export default Posts;
